sys_script_include.script:
var TotalCostForPO = Class.create();
TotalCostForPO.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	totalCost:function()
	{
		var id = this.getParameter('sysparm_id');
		var gr = new GlideRecord('x_1321711_ordering_purchase_order_items');
		gr.addQuery('u_op_po_items_purchase_order.u_op_po_numberSTARTSWITH'+id);
		gr.query();

		var total = 0;
		while(gr.next())
		{
			total = total + Number(gr.u_op_po_items_total_cost);
		}

		return total;
	},

    type: 'TotalCostForPO'
});
